import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar, AreaChart, Area, PieChart, Pie, Cell } from 'recharts';
import { FiRefreshCw, FiTrendingUp, FiTrendingDown, FiActivity, FiDatabase, FiClock, FiAlertCircle } from 'react-icons/fi';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [historicalData, setHistoricalData] = useState(null);
  const [statistics, setStatistics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [autoRefresh, setAutoRefresh] = useState(true);

  const API_BASE_URL = 'http://127.0.0.1:8000';

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/dashboard-data`);
      if (!response.ok) throw new Error('Failed to fetch dashboard data');
      const data = await response.json();
      setDashboardData(data);
      setLastUpdated(new Date());
      setError(null);
    } catch (err) {
      setError('Failed to load dashboard data. Please ensure the backend is running.');
      console.error('Dashboard data error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch historical data
  const fetchHistoricalData = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/historical-data`);
      if (!response.ok) throw new Error('Failed to fetch historical data');
      const data = await response.json();
      setHistoricalData(data);
    } catch (err) {
      console.error('Historical data error:', err);
    }
  };

  // Fetch statistics
  const fetchStatistics = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/statistics`);
      if (!response.ok) throw new Error('Failed to fetch statistics');
      const data = await response.json();
      setStatistics(data);
    } catch (err) {
      console.error('Statistics error:', err);
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchDashboardData();
    fetchHistoricalData();
    fetchStatistics();
  }, []);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    if (!autoRefresh) return;
    
    const interval = setInterval(() => {
      fetchDashboardData();
    }, 30000);

    return () => clearInterval(interval);
  }, [autoRefresh]);

  // Loading state
  if (loading && !dashboardData) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-xl text-gray-600">Loading dashboard data...</p>
          <p className="text-sm text-gray-500 mt-2">Please ensure the backend server is running</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center max-w-md mx-auto p-6">
          <FiAlertCircle className="text-red-500 text-6xl mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Connection Error</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={fetchDashboardData}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition-colors"
          >
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  const currentLevels = dashboardData?.current_levels || {};
  const timeSeries = dashboardData?.time_series || [];
  const featureImportance = dashboardData?.feature_importance || [];
  const stats = dashboardData?.statistics || {};

  return (
    <div className="space-y-8 p-6">
      {/* Header with Controls */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h1 className="text-4xl lg:text-5xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
            Air Quality Dashboard
          </h1>
          <p className="text-lg text-gray-600 mt-2">
            Real-time monitoring and AI-powered predictions
          </p>
        </div>
        
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-2">
            <input
              type="checkbox"
              id="autoRefresh"
              checked={autoRefresh}
              onChange={(e) => setAutoRefresh(e.target.checked)}
              className="rounded"
            />
            <label htmlFor="autoRefresh" className="text-sm text-gray-600">
              Auto-refresh
            </label>
          </div>
          
          <button
            onClick={fetchDashboardData}
            className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            <FiRefreshCw className={`${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
          
          {lastUpdated && (
            <div className="text-sm text-gray-500 flex items-center gap-1">
              <FiClock />
              {lastUpdated.toLocaleTimeString()}
            </div>
          )}
        </div>
      </div>

      {/* Quick Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(currentLevels).map(([pollutant, data]) => (
          <div
            key={pollutant}
            className={`${data.bgColor} ${data.borderColor} border-2 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1`}
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold text-gray-800">{pollutant}</h3>
              <div className={`w-3 h-3 rounded-full ${data.color.replace('text-', 'bg-')}`}></div>
            </div>
            
            <div className={`text-4xl font-bold ${data.color} mb-3`}>
              {data.value.toFixed(2)}
              <span className="text-lg text-gray-600 ml-1">{data.unit}</span>
            </div>
            
            <div className="flex items-center justify-between">
              <span className={`inline-block px-3 py-1 text-sm font-semibold rounded-full ${
                data.status === 'Good' ? 'bg-green-100 text-green-800' :
                data.status === 'Moderate' ? 'bg-yellow-100 text-yellow-800' :
                data.status === 'Poor' ? 'bg-orange-100 text-orange-800' :
                'bg-red-100 text-red-800'
              }`}>
                {data.status}
              </span>
              
              <div className="flex items-center gap-1 text-sm text-gray-600">
                <FiActivity />
                Live
              </div>
            </div>
          </div>
        ))}
        
        {/* System Status Card */}
        <div className="bg-gradient-to-br from-indigo-50 to-purple-50 border-2 border-indigo-200 rounded-2xl p-6 shadow-lg">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-12 h-12 bg-gradient-to-br from-indigo-500 to-purple-500 rounded-xl flex items-center justify-center">
              <FiDatabase className="text-white text-xl" />
            </div>
            <div>
              <h3 className="text-xl font-semibold text-gray-800">System Status</h3>
              <p className="text-sm text-gray-600">Data Quality & Performance</p>
            </div>
          </div>
          
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Data Records</span>
              <span className="font-semibold">{stats.total_records || 'N/A'}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Data Quality</span>
              <span className="font-semibold text-green-600">
                {stats.data_quality_percentage || 'N/A'}%
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Features</span>
              <span className="font-semibold">{stats.features_count || 'N/A'}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Enhanced Time Series Chart */}
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
          <div className="mb-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-2">Pollutant Trends</h3>
            <p className="text-gray-600">Real-time monitoring over 24 hours</p>
          </div>
          
          {timeSeries.length > 0 ? (
            <ResponsiveContainer width="100%" height={350}>
              <AreaChart data={timeSeries}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f1f5f9" />
                <XAxis dataKey="time" stroke="#64748b" />
                <YAxis stroke="#64748b" />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '12px',
                    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Legend />
                <Area type="monotone" dataKey="CO" stackId="1" stroke="#3b82f6" fill="#3b82f6" fillOpacity={0.6} />
                <Area type="monotone" dataKey="NO2" stackId="1" stroke="#ef4444" fill="#ef4444" fillOpacity={0.6} />
                <Area type="monotone" dataKey="C6H6" stackId="1" stroke="#10b981" fill="#10b981" fillOpacity={0.6} />
              </AreaChart>
            </ResponsiveContainer>
          ) : (
            <div className="h-[350px] flex items-center justify-center text-gray-500">
              <div className="text-center">
                <FiActivity className="text-4xl mx-auto mb-2" />
                <p>No time series data available</p>
              </div>
            </div>
          )}
        </div>

        {/* Feature Importance Chart */}
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
          <div className="mb-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-2">Feature Importance</h3>
            <p className="text-gray-600">Most influential factors in predictions</p>
          </div>
          
          {featureImportance.length > 0 ? (
            <ResponsiveContainer width="100%" height={350}>
              <BarChart data={featureImportance} layout="horizontal">
                <CartesianGrid strokeDasharray="3 3" stroke="#f1f5f9" />
                <XAxis type="number" domain={[0, 'dataMax + 0.05']} stroke="#64748b" />
                <YAxis dataKey="feature" type="category" width={120} stroke="#64748b" />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '12px',
                    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Bar dataKey="importance" fill="#3b82f6" radius={[0, 4, 4, 0]} />
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <div className="h-[350px] flex items-center justify-center text-gray-500">
              <div className="text-center">
                <FiTrendingUp className="text-4xl mx-auto mb-2" />
                <p>Feature importance data not available</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Additional Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Temperature and Humidity Chart */}
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
          <div className="mb-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-2">Environmental Factors</h3>
            <p className="text-gray-600">Temperature and humidity trends</p>
          </div>
          
          {timeSeries.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={timeSeries}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f1f5f9" />
                <XAxis dataKey="time" stroke="#64748b" />
                <YAxis yAxisId="left" stroke="#3b82f6" />
                <YAxis yAxisId="right" orientation="right" stroke="#10b981" />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'rgba(255, 255, 255, 0.95)',
                    border: 'none',
                    borderRadius: '12px',
                    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)'
                  }}
                />
                <Legend />
                <Line yAxisId="left" type="monotone" dataKey="temperature" stroke="#3b82f6" strokeWidth={3} dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }} />
                <Line yAxisId="right" type="monotone" dataKey="humidity" stroke="#10b981" strokeWidth={3} dot={{ fill: '#10b981', strokeWidth: 2, r: 4 }} />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <div className="h-[300px] flex items-center justify-center text-gray-500">
              <p>No environmental data available</p>
            </div>
          )}
        </div>

        {/* Data Distribution Pie Chart */}
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
          <div className="mb-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-2">Data Distribution</h3>
            <p className="text-gray-600">Dataset overview and quality metrics</p>
          </div>
          
          {statistics && statistics.dataset_info ? (
            <div className="h-[300px] flex items-center justify-center">
              <div className="text-center">
                <div className="w-32 h-32 mx-auto mb-4">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={[
                          { name: 'Valid Data', value: statistics.dataset_info.data_quality_percentage || 0, color: '#10b981' },
                          { name: 'Missing Data', value: 100 - (statistics.dataset_info.data_quality_percentage || 0), color: '#ef4444' }
                        ]}
                        cx="50%"
                        cy="50%"
                        innerRadius={30}
                        outerRadius={60}
                        paddingAngle={5}
                        dataKey="value"
                      >
                        {[
                          { name: 'Valid Data', value: statistics.dataset_info.data_quality_percentage || 0, color: '#10b981' },
                          { name: 'Missing Data', value: 100 - (statistics.dataset_info.data_quality_percentage || 0), color: '#ef4444' }
                        ].map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Total Records:</span>
                    <span className="font-semibold">{statistics.dataset_info.total_records || 'N/A'}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Features:</span>
                    <span className="font-semibold">{statistics.dataset_info.features || 'N/A'}</span>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="h-[300px] flex items-center justify-center text-gray-500">
              <p>Statistics not available</p>
            </div>
          )}
        </div>
      </div>

      {/* System Features */}
      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl shadow-xl p-8 border border-blue-100">
        <div className="text-center mb-8">
          <h3 className="text-3xl font-bold text-gray-800 mb-3">Advanced Features</h3>
          <p className="text-xl text-gray-600">Powered by cutting-edge AI and real-time data</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-purple-500 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg group-hover:shadow-xl transition-all duration-300 transform group-hover:scale-110">
              <span className="text-3xl">🤖</span>
            </div>
            <h4 className="text-xl font-bold text-gray-800 mb-3">XGBoost AI</h4>
            <p className="text-gray-600 leading-relaxed">Advanced machine learning with state-of-the-art performance</p>
          </div>
          
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-green-500 to-blue-500 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg group-hover:shadow-xl transition-all duration-300 transform group-hover:scale-110">
              <span className="text-3xl">📊</span>
            </div>
            <h4 className="text-xl font-bold text-gray-800 mb-3">Real-time Data</h4>
            <p className="text-gray-600 leading-relaxed">Live monitoring with 30-second auto-refresh</p>
          </div>
          
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-purple-500 to-pink-500 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg group-hover:shadow-xl transition-all duration-300 transform group-hover:scale-110">
              <span className="text-3xl">🔬</span>
            </div>
            <h4 className="text-xl font-bold text-gray-800 mb-3">UCI Dataset</h4>
            <p className="text-gray-600 leading-relaxed">Based on scientific UCI Air Quality standards</p>
          </div>
          
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-orange-500 to-red-500 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg group-hover:shadow-xl transition-all duration-300 transform group-hover:scale-110">
              <span className="text-3xl">📈</span>
            </div>
            <h4 className="text-xl font-bold text-gray-800 mb-3">Predictive Analytics</h4>
            <p className="text-gray-600 leading-relaxed">Forecast air quality with high accuracy</p>
          </div>
        </div>
      </div>

      {/* Footer Info */}
      <div className="text-center py-6 text-gray-500">
        <p className="text-sm">
          Dashboard updates automatically every 30 seconds • 
          Last updated: {lastUpdated ? lastUpdated.toLocaleString() : 'Never'} • 
          Data source: UCI Air Quality Dataset
        </p>
      </div>
    </div>
  );
};

export default Dashboard;
