import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import toast from 'react-hot-toast';

const PredictionForm = ({ onModelTrained }) => {
  const [isTraining, setIsTraining] = useState(false);
  const [isPredicting, setIsPredicting] = useState(false);
  const [predictions, setPredictions] = useState(null);
  const [modelAccuracy, setModelAccuracy] = useState(null);

  const { register, handleSubmit, formState: { errors }, reset } = useForm();

  const onSubmit = async (data) => {
    setIsPredicting(true);
    try {
      const response = await fetch('/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Prediction failed');
      }

      const result = await response.json();
      setPredictions(result.predictions);
      toast.success('Prediction completed successfully!');
    } catch (error) {
      console.error('Prediction error:', error);
      toast.error('Prediction failed. Please try again.');
    } finally {
      setIsPredicting(false);
    }
  };

  const handleTrainModel = async () => {
    setIsTraining(true);
    try {
      const response = await fetch('/train', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Training failed');
      }

      const result = await response.json();
      setModelAccuracy(result.accuracy);
      onModelTrained(result.accuracy);
      toast.success('Model trained successfully!');
    } catch (error) {
      console.error('Training error:', error);
      toast.error('Model training failed. Please try again.');
    } finally {
      setIsTraining(false);
    }
  };

  const handleDownloadDataset = async () => {
    try {
      const response = await fetch('/download-dataset', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Dataset download failed');
      }

      toast.success('Dataset downloaded successfully!');
    } catch (error) {
      console.error('Download error:', error);
      toast.error('Dataset download failed. Please try again.');
    }
  };

  const getPollutantStatus = (value, pollutant) => {
    const thresholds = {
      CO: { good: 2, moderate: 4, poor: 6 },
      NO2: { good: 40, moderate: 80, poor: 120 },
      C6H6: { good: 5, moderate: 10, poor: 15 },
      PM10: { good: 20, moderate: 50, poor: 100 }
    };

    const threshold = thresholds[pollutant];
    if (value <= threshold.good) return { status: 'Good', color: 'text-green-600', bg: 'bg-green-100' };
    if (value <= threshold.moderate) return { status: 'Moderate', color: 'text-yellow-600', bg: 'bg-yellow-100' };
    return { status: 'Poor', color: 'text-red-600', bg: 'bg-red-100' };
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Air Quality Prediction</h1>
        <p className="text-lg text-gray-600">
          Input environmental parameters to predict air quality pollutant levels
        </p>
      </div>

      {/* Model Training Section */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Model Management</h3>
          <p className="card-subtitle">Train the model and download dataset</p>
        </div>
        <div className="flex space-x-4">
          <button
            onClick={handleTrainModel}
            disabled={isTraining}
            className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isTraining ? 'Training...' : 'Train Model'}
          </button>
          <button
            onClick={handleDownloadDataset}
            className="btn-secondary"
          >
            Download Dataset
          </button>
        </div>
        {modelAccuracy && (
          <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg">
            <p className="text-green-800">
              <strong>Model Accuracy:</strong> {JSON.stringify(modelAccuracy, null, 2)}
            </p>
          </div>
        )}
      </div>

      {/* Prediction Form */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Input Parameters</h3>
          <p className="card-subtitle">Enter environmental and sensor data for prediction</p>
        </div>
        
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Environmental Parameters */}
            <div className="space-y-4">
              <h4 className="font-semibold text-gray-900">Environmental Parameters</h4>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Temperature (°C)
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('temperature', { required: 'Temperature is required' })}
                  className="input-field"
                  placeholder="25.5"
                />
                {errors.temperature && (
                  <p className="text-red-600 text-sm mt-1">{errors.temperature.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Humidity (%)
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('humidity', { required: 'Humidity is required' })}
                  className="input-field"
                  placeholder="60.0"
                />
                {errors.humidity && (
                  <p className="text-red-600 text-sm mt-1">{errors.humidity.message}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Absolute Humidity (g/m³)
                </label>
                <input
                  type="number"
                  step="0.001"
                  {...register('absolute_humidity')}
                  className="input-field"
                  placeholder="0.8"
                />
              </div>
            </div>

            {/* Sensor Parameters */}
            <div className="space-y-4">
              <h4 className="font-semibold text-gray-900">Sensor Parameters</h4>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  PT08.S1(CO) - CO Sensor
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('pt08_s1')}
                  className="input-field"
                  placeholder="1200.0"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  PT08.S2(NMHC) - NMHC Sensor
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('pt08_s2')}
                  className="input-field"
                  placeholder="1000.0"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  PT08.S3(NOx) - NOx Sensor
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('pt08_s3')}
                  className="input-field"
                  placeholder="1100.0"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  PT08.S4(NO2) - NO2 Sensor
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('pt08_s4')}
                  className="input-field"
                  placeholder="1500.0"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  PT08.S5(O3) - O3 Sensor
                </label>
                <input
                  type="number"
                  step="0.1"
                  {...register('pt08_s5')}
                  className="input-field"
                  placeholder="1000.0"
                />
              </div>
            </div>
          </div>

          <div className="flex space-x-4">
            <button
              type="submit"
              disabled={isPredicting}
              className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isPredicting ? 'Predicting...' : 'Get Prediction'}
            </button>
            <button
              type="button"
              onClick={() => reset()}
              className="btn-secondary"
            >
              Reset Form
            </button>
          </div>
        </form>
      </div>

      {/* Predictions Display */}
      {predictions && (
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Prediction Results</h3>
            <p className="card-subtitle">Air quality pollutant levels</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {Object.entries(predictions).map(([pollutant, value]) => {
              const status = getPollutantStatus(value, pollutant);
              return (
                <div key={pollutant} className="text-center p-4 rounded-lg border">
                  <h4 className="font-semibold text-gray-900 mb-2">{pollutant}</h4>
                  <div className={`text-2xl font-bold ${status.color} mb-2`}>
                    {value.toFixed(2)}
                  </div>
                  <span className={`inline-block px-3 py-1 text-sm font-medium ${status.bg} ${status.color} rounded-full`}>
                    {status.status}
                  </span>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default PredictionForm;
