import React, { useState, useEffect } from 'react';

const ModelStatus = ({ status, onStatusUpdate }) => {
  const [modelInfo, setModelInfo] = useState(null);

  useEffect(() => {
    if (status.isLoaded) {
      fetchModelInfo();
    }
  }, [status.isLoaded]);

  const fetchModelInfo = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/model-info');
      if (response.ok) {
        const data = await response.json();
        setModelInfo(data);
      }
    } catch (error) {
      console.error('Error fetching model info:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRefresh = () => {
    onStatusUpdate();
    if (status.isLoaded) {
      fetchModelInfo();
    }
  };

  const getStatusColor = (isLoaded) => {
    return isLoaded ? 'text-green-600' : 'text-red-600';
  };

  const getStatusIcon = (isLoaded) => {
    return isLoaded ? '‚úÖ' : '‚ùå';
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Model Status</h1>
        <p className="text-lg text-gray-600">
          Monitor the status and performance of your air quality prediction model
        </p>
      </div>

      {/* Status Overview */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">System Status</h3>
          <p className="card-subtitle">Current model and system health</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Model Status */}
          <div className="text-center p-4 border rounded-lg">
            <div className="text-3xl mb-2">{getStatusIcon(status.isLoaded)}</div>
            <h4 className="font-semibold text-gray-900 mb-1">Model Status</h4>
            <p className={`font-medium ${getStatusColor(status.isLoaded)}`}>
              {status.isLoaded ? 'Ready' : 'Not Ready'}
            </p>
          </div>

          {/* Last Training */}
          <div className="text-center p-4 border rounded-lg">
            <div className="text-3xl mb-2">üïí</div>
            <h4 className="font-semibold text-gray-900 mb-1">Last Trained</h4>
            <p className="text-gray-600">
              {status.lastTrained 
                ? new Date(status.lastTrained).toLocaleDateString()
                : 'Never'
              }
            </p>
          </div>

          {/* API Health */}
          <div className="text-center p-4 border rounded-lg">
            <div className="text-3xl mb-2">üåê</div>
            <h4 className="font-semibold text-gray-900 mb-1">API Health</h4>
            <p className="text-green-600 font-medium">Healthy</p>
          </div>
        </div>

        <div className="mt-6 flex justify-center">
          <button
            onClick={handleRefresh}
            className="btn-primary"
          >
            Refresh Status
          </button>
        </div>
      </div>

      {/* Model Information */}
      {status.isLoaded && modelInfo && (
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Model Information</h3>
            <p className="card-subtitle">Technical details about the trained model</p>
          </div>
          
          <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Model Type</h4>
                <p className="text-gray-600">{modelInfo.model_type}</p>
              </div>
              
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Target Variables</h4>
                <div className="flex flex-wrap gap-2">
                  {modelInfo.targets?.map((target, index) => (
                    <span key={index} className="px-2 py-1 bg-primary-100 text-primary-800 text-sm rounded">
                      {target}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            {modelInfo.features && (
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Input Features</h4>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
                  {modelInfo.features.map((feature, index) => (
                    <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 text-sm rounded">
                      {feature}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {modelInfo.model_accuracy && (
              <div>
                <h4 className="font-semibold text-gray-900 mb-2">Model Performance</h4>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <pre className="text-sm text-gray-700 whitespace-pre-wrap">
                    {JSON.stringify(modelInfo.model_accuracy, null, 2)}
                  </pre>
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Model Training History */}
      {status.isLoaded && (
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Training History</h3>
            <p className="card-subtitle">Model training and performance metrics</p>
          </div>
          
          <div className="space-y-4">
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div className="flex items-center space-x-2">
                <span className="text-blue-600">‚ÑπÔ∏è</span>
                <p className="text-blue-800">
                  <strong>Note:</strong> The model has been successfully trained and is ready for predictions. 
                  You can view detailed accuracy metrics above and make predictions using the Predict tab.
                </p>
              </div>
            </div>

            {status.accuracy && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                <h4 className="font-semibold text-green-800 mb-2">Latest Training Results</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {Object.entries(status.accuracy).map(([target, metrics]) => (
                    <div key={target} className="border border-green-200 rounded p-3 bg-white">
                      <h5 className="font-medium text-green-800 mb-2">{target}</h5>
                      <div className="space-y-1 text-sm">
                        <div className="flex justify-between">
                          <span>R¬≤ Score:</span>
                          <span className="font-medium">{(metrics.r2 * 100).toFixed(1)}%</span>
                        </div>
                        <div className="flex justify-between">
                          <span>RMSE:</span>
                          <span className="font-medium">{metrics.rmse.toFixed(3)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span>MAE:</span>
                          <span className="font-medium">{metrics.mae.toFixed(3)}</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* System Requirements */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">System Requirements</h3>
          <p className="card-subtitle">What you need to run the system</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h4 className="font-semibold text-gray-900 mb-3">Backend Requirements</h4>
            <ul className="space-y-2 text-gray-600">
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>Python 3.8+</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>Flask Framework</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>XGBoost & Scikit-learn</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>UCI Air Quality Dataset</span>
              </li>
            </ul>
          </div>
          
          <div>
            <h4 className="font-semibold text-gray-900 mb-3">Frontend Requirements</h4>
            <ul className="space-y-2 text-gray-600">
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>React 18+</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>Node.js 16+</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>Modern Web Browser</span>
              </li>
              <li className="flex items-center space-x-2">
                <span className="text-green-500">‚úì</span>
                <span>Internet Connection</span>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModelStatus;
